function handler_PU1_onSelectionChange(newValue, oldValue){
     P1 = app.getValue("PU1").value;
     app.enabled(["PU2"], true);
     switch (P1) {
    case '6':
		app.setValue("LP1", '--------X--------');
		PA1 = '7';
		break;
	case '7':
		app.setValue("LP1", '-----------------');
		PA1 = '7';
		break;
	case '8':
